import React from 'react';
import Popup from "reactjs-popup";
import './Card.css';

function Card({img, name, description, pk }) {

	function petSelected() {
		console.log(pk);

	}

	return (

			<article onClick={petSelected} className="pointer bg-washed-green dib br3 ma2 pa4-ns mv3 ba b--black-10 shadow-3 bg-animate hover-bg-light-green">
			  <div className="tc">
			    <img src={img} className="br-100 h4 w4 dib ba b--near-white pa1" alt="This is an avatar" />
			    <h1 className="f4 mb2 dark-grey">{name}</h1>
			    <h2 className="f6 fw4 dark-grey mt0">{description}</h2>
			  </div>
			  <Popup trigger={<a className="f6 no-underline br-pill ph3 pv2 mb2 dib white bg-mid-gray" href="#0">More</a>} modal closeOnDocumentClick>
    			<span> Modal content </span>
  			</Popup>
			</article>
	);
}



export default Card;
