import React from 'react';

function _calculateAge(birthday) { // birthday is a date
    var diff_ms = new Date.now() - birthday.getTime();
    var age_dt = new Date(diff_ms); 

    return Math.abs(age_dt.getUTCFullYear() - 1970);
}

function PetDetails({ isPetSelected, onPetSelection, selectedPetPk, images, pets}) {
	console.log(isPetSelected);

	const pet = pets.find((pet) => pet.id === selectedPetPk);
	const petImages = images.filter((img) => img.pets_id === selectedPetPk);
	const age = _calculateAge(pet.birthday);

	console.log(pet);
	console.log(petImages);

	return(
		<div>
			<p className='pointer tl pa3' onClick={onPetSelection}>{'<- Back to search'}</p>
			<article className="pointer bg-washed-green dib br3 ma2 pa4-ns mv3 ba b--black-10 shadow-3 bg-animate hover-bg-light-green">
		  <div className="tl">
{/*		    <img src={img} className="br-100 h4 w4 dib ba b--near-white pa1" alt="This is an avatar" /> */}
		    <h1 className="f4 mb2 dark-grey">Name: {pet.name}</h1>
		    <h1 className="f4 mb2 dark-grey">Gender: {pet.gender}</h1>
		    <h1 className="f4 mb2 dark-grey">Age: {age}</h1>
		    <h2 className="f6 fw4 dark-grey mt0">Description: {pet.description}</h2>
		  </div>
		</article>
		</div>
	) 
}

export default PetDetails;
