import React from 'react';

function formatBirthday(birthday) {
	const year = birthday.substring(0, 4);
	const month = birthday.substring(5, 7);
	const day = birthday.substring(8, 10);

	return day + '-' + month + '-' + year;
}

function capitalizeFirstLetter(string) {
    return string[0].toUpperCase() + string.slice(1);
}

function PetDetails({ isPetSelected, onPetSelection, selectedPetPk, images, pets, onSearchChange}) {
	console.log(isPetSelected);

	const pet = pets.find((pet) => pet.id === selectedPetPk);
	const petImages = images.filter((img) => img.pets_id === selectedPetPk);

	const gender = capitalizeFirstLetter(pet.gender);
	const birthday = formatBirthday(pet.birthday);
	console.log(pet);
	console.log(petImages);

	return(
		<div>
			<p className='pointer tl pa3' onClick={(event) => {onPetSelection(); onSearchChange(event);}}>{'<- Back to search'}</p>
			<article className="pointer bg-washed-green dib br3 ma2 pa4-ns mv3 ba b--black-10 shadow-3 bg-animate hover-bg-light-green">
		  <div className="tl">
{/*		    <img src={img} className="br-100 h4 w4 dib ba b--near-white pa1" alt="This is an avatar" /> */}
		    <h1 className="f3 mb2 dark-grey">Name: {pet.name}</h1>
		    <h1 className="f3 mb2 dark-grey">Gender: {gender}</h1>
		    <h1 className="f3 mb2 dark-grey">Birthday: {birthday}</h1>
		    <h1 className="f3 mb2 dark-grey">Description: {pet.description}</h1>
		  </div>
		</article>
		</div>
	) 
}

export default PetDetails;
