import React from 'react';
import Popup from "reactjs-popup";
import './PetDetails.css';

function formatBirthday(birthday) {
	const year = birthday.substring(0, 4);
	const month = birthday.substring(5, 7);
	const day = birthday.substring(8, 10);

	return day + '-' + month + '-' + year;
}

function capitalizeFirstLetter(string) {
    return string[0].toUpperCase() + string.slice(1);
}

function PetDetails({ isPetSelected, onPetSelection, selectedPetPk, images, pets }) {
	console.log(isPetSelected);

	const pet = pets.find((pet) => pet.id === selectedPetPk);
	const petImages = images.filter((img) => img.pets_id === selectedPetPk);

	const gender = capitalizeFirstLetter(pet.gender);
	const birthday = formatBirthday(pet.birthday);
	console.log(pet);
	console.log(petImages);

	const contentStyle = {
	  maxWidth: "600px",
	  maxHeight: "600px",
	  width: "90%"
	};

	let imgs = [];
	petImages.forEach((img) => {
  	imgs.push(
  		<Popup
		    trigger={<img src={'http://localhost:4412/api/file/' + img.url} className="br-100 h4 w4 dib ba b--near-white pa1" alt="This is an avatar" />}
		    modal
		    contentStyle={contentStyle}
  		>
			    {close => (
			      <div className="modal">
			        <a className="close" onClick={close}>
			          &times;
			        </a>
			        <div className="header"> Modal Title </div>
			        <div className="content">
			          {" "}
			          Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque, a
			          nostrum. Dolorem, repellat quidem ut, minima sint vel eveniet
			          quibusdam voluptates delectus doloremque, explicabo tempore dicta
			          adipisci fugit amet dignissimos?
			          <br />
			          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Consequatur
			          sit commodi beatae optio voluptatum sed eius cumque, delectus saepe
			          repudiandae explicabo nemo nam libero ad, doloribus, voluptas rem
			          alias. Vitae?
			        </div>
			        <div className="actions">
			          <Popup
			            trigger={<button className="button"> Menu Demo </button>}
			            position="top center"
			            closeOnDocumentClick
			            contentStyle={{ padding: "0px", border: "none" }}
			          >
			            <div className="menu">
			              <div className="menu-item"> Menu item 1</div>
			              <div className="menu-item"> Menu item 2</div>
			              <div className="menu-item"> Menu item 3</div>
			              <Popup
			                trigger={<div className="menu-item"> sup Menu </div>}
			                position="right top"
			                on="hover"
			                closeOnDocumentClick
			                mouseLeaveDelay={300}
			                mouseEnterDelay={0}
			                contentStyle={{ padding: "0px", border: "none" }}
			                arrow={false}
			              >

			              </Popup>

			            </div>
			          </Popup>
			          <button
			            className="button"
			            onClick={() => {
			              console.log("modal closed ");
			              close();
			            }}
			          >
			            close modal
			          </button>
			        </div>
			      </div>
			    )}
			  </Popup>
  	);
	})



	return(
		<div>
			<p className='pointer tl pa3' onClick={onPetSelection}>{'<- Back to search'}</p>
			<article className="w-75 pointer bg-washed-green dib br3 ma2 pa4-ns mv3 ba b--black-10 shadow-3 bg-animate hover-bg-light-green">
		  <div className="tl pa3">
		  	<div className="tc">{imgs}</div>
		    <h1 className="f3 mb2 dark-grey">Name: {pet.name}</h1>
		    <h1 className="f3 mb2 dark-grey">Gender: {gender}</h1>
		    <h1 className="f3 mb2 dark-grey">Birthday: {birthday}</h1>
		    <h1 className="f3 mb2 dark-grey ow">Description: {pet.description}</h1>
		  </div>
		</article>
		</div>
	) 
}

export default PetDetails;


