import React from 'react';
import ErrorBoundary from '../components/ErrorBoundary';
import Nav from '../components/Nav';
import Scroll from '../components/Scroll'
import CardList from '../components/CardList';
import Searchbox from '../components/Searchbox';

class App extends React.Component {
	constructor() {
		super()
		this.state = {
			'pets': [],
			'images': [],
			'searchField': '',
			'isPetSelected': false,
			'selectedPetPk': '',
			'petLikes': []
		}
	}

	componentDidMount() {
		fetch('http://localhost:4412/api/pets')
		.then(response => response.json())
		.then(response => this.setState({pets: response}));

		fetch('http://localhost:4412/api/images')
		.then(response => response.json())
		.then(response => this.setState({images: response}));
	}

	onSearchChange = (event) => {
		this.setState({ searchField: event.target.value });
	}

	onPetSelection = (pk) => {
		this.setState({isPetSelected: !this.state.isPetSelected, selectedPetPk: pk, searchField: ''});
		fetch('http://localhost:4412/api/likes' + selectedPetPk)
		.then(response => response.json())
		.then(response => this.setState({petLikes: response}));
	}

	render() {
		const { pets, searchField, images, isPetSelected, selectedPetPk, filter } = this.state;
		const filteredPets = pets.filter(pet => {
			return pet.name.toLowerCase().includes(searchField.toLowerCase()) ||
						pet.type.toLowerCase().includes(searchField.toLowerCase()) ||
						pet.description.toLowerCase().includes(searchField.toLowerCase()) ||
						pet.gender.toLowerCase().match(searchField.toLowerCase());
		})
		return !pets.length ?
			<h1 className="tc">Loading</h1> :
			(
				<div className="tc">
					<Nav />
					{!isPetSelected ?
						<div>
							<Searchbox searchChange={this.onSearchChange}/>
							<Scroll>
								<ErrorBoundary>
									<CardList images={images} pets={filteredPets} onPetSelection={this.onPetSelection} isPetSelected={isPetSelected} selectedPetPk={selectedPetPk}/>
								</ErrorBoundary>
							</Scroll>
						</div> :
						<Scroll>
							<ErrorBoundary>
								<CardList searchChange={this.onSearchChange} images={images} pets={filteredPets} onPetSelection={this.onPetSelection} isPetSelected={isPetSelected} selectedPetPk={selectedPetPk}/>
							</ErrorBoundary>
						</Scroll>
					}
				</div>
			);
	}
}

export default App;
